import java.util.*; public class RPS { public static void main(String[] args){ int hnum = 4; Scanner ui = new Scanner(System.in); System.out.println("player one R,P,S"); String p1 = ui.nextLine(); System.out.println("player two R,P,S"); String p2 = ui.nextLine(); if(p1==p2){ System.out.println("Tie");} else if((p1 == "R" && p2 == "S") || (p1=="S"&&p2=="P")|| (p1=="P"&&p2=="R")){System.out.println("Player one wins");} else{System.out.println("Player two wins");} } }