import java.util.*; import java.lang.Math; public class basicmath { public static void main(String[] args) { Scanner ui = new Scanner(System.in); Random rand = new Random(); int points = 0; double total = 0; for (int i = 0; i < 10; i++) { int num1 = ((int) (Math.random() * 10)); int num2 = rand.nextInt(10) + 1; double answer = 0.0; double input = 0.0; int sign = rand.nextInt(4); if (sign == 0) { System.out.printf("What is %d - %d \n", num1, num2); answer = (double) num1 - num2; } else if (sign == 1) { System.out.printf("What is %d + %d \n", num1, num2); answer = (double) num1 + num2; } else if (sign == 2) { System.out.printf("What is %d / %d \n", num1, num2); answer = (double) num1 / num2; } else { System.out.printf("What is %d * %d \n", num1, num2); answer = (double) num1 * num2; } input = ui.nextDouble(); if (input == answer) { System.out.println("Wow thats correct!"); points++; total++; } else { System.out.printf("Oo so close, the answer was %f \n", answer); points--; total++; } System.out.println("accuracy " + ((points / total)*100)); } } }